import { HttpClient } from '@angular/common/http';
import { Injectable, Query } from '@angular/core';
import { SearchGIfsResponse, Gif } from '../interface/gif.Interface';

@Injectable({
  providedIn: 'root'
})
export class GifServiceService {

  private _historial: string[] = [];
  private apiKey: string = 'J9EkEQEwk1JedQ7LtzSEFCbQFDIqy9fH'

  public resultados: Gif[] = [];

  get historial() {
    return [...this._historial]
  }

  constructor(private http: HttpClient) {


      this._historial = JSON.parse(localStorage.getItem('Historial')! ) || [];


  }

  buscarGif(query: string = '') {
    query = query.trim().toLocaleLowerCase()
    if(!this._historial.includes(query)){
      this._historial.unshift(query);
      this._historial = this._historial.splice(0.9)

      localStorage.setItem('Historial', JSON.stringify( this._historial ));
    }

    this.http.get<SearchGIfsResponse>(`https://api.giphy.com/v1/gifs/search?api_key=J9EkEQEwk1JedQ7LtzSEFCbQFDIqy9fH&q=${ query }&limit=10`)
    .subscribe( (resp) => {
      console.log(resp.data );
      this.resultados = resp.data
    })
  }


}
